    def update_fio(self):
        id = self.lineEdit_3.text()
        fio = self.lineEdit.text()

        if len(id) == 0:
             self.label.setText("Введите ID!")
             return

        if len(fio) == 0:
             print("Пусто! ФИО")
             return
        
        try:
            mycursor.execute("UPDATE user SET fio='" + fio +"' WHERE id=" + id)
            result = mycursor.fetchall
            mydb.commit()

            self.label.setText("Успеешно изменено - ФИО")

        except mc.Error as e:
            print("НЕТ, ПЕРЕДЕЛЫВАЙ! ФИО")


    def update_phone(self):
        id = self.lineEdit_3.text()
