    def update_password(self):
        id = self.lineEdit_3.text()
        password = self.lineEdit_2.text()

        if len(id) == 0:
             self.label.setText("Введите ID!")
             return

        if len(password) == 0:
             print("Пусто! Пароль")
             return
        
        try:
            mycursor.execute("UPDATE user SET password='" + password +"' WHERE id=" + id)
            result = mycursor.fetchall
            mydb.commit()

            self.label.setText("Успеешно изменено - Пароль")

        except mc.Error as e:
            print("НЕТ, ПЕРЕДЕЛЫВАЙ! Пароль")
        
